#! /usr/bin/env python3
import re

#question number 1
def dna_nt60(dna, n):
    new_dna = [(dna[i:i+n]) for i in range(0, len(dna), n)]
    new_dna_out="\n".join(new_dna)
    return new_dna_out

dna = ''
new_dna = dna_nt60(dna, 10)
print(f'This is the new dna output split at every 60 nucleotides: \n{new_dna}')
print(repr(new_dna))
